<?phpinclude_once("../include/configSQL.php");include_once("../include/funciones.php");switch ($_REQUEST['funcion']) {    case "editar":        Editar($_REQUEST['id']);        break;    case 1:        echo "i es igual a 1";        break;    case 2:        echo "i es igual a 2";        break;}function Editar($id){$con=Conectar();$sql="select * from articulo where ar_id=$id";$result=mysql_query($sql);$r="<div class='box'><h2>Editar</h2><div class='content-separator'></div><form name='FrmEditarPubli' id='FrmEditarPubli' method='post' action='?' ><table class='data-table'><input type='hidden' name='modulo' value='publicacion'><input type='hidden' name='accion' value='actualizar'><input type='hidden' name='id' value='".$id."'>";while($row = mysql_fetch_array($result)){$t='document.getElementById("c_v").style.display="none"';$r.="<tr>	<td><center><strong>*Titulo:</strong></center></td>	<td><input class='input' type='text' name='titulo' id='titulo' size='50' value='".$row['ar_titulo']."'></td></tr><tr>	<td><center><strong>*Botones Redes:</strong></center></td>	<td>".SiNoSelect($row['ar_botones'],"botones")."</td></tr><tr>	<td><center><strong>*Menu:</strong></center></td>	<td>".SelectMenu($row['ar_menu'])."</td></tr><tr>	<td colspan='2'><span id='c_v' ><input class='boton' type='button' onclick='".$t.";CargarTimyMCE()' style='float: right;' value='EDITAR'>".$row['ar_contenido']."</span><textarea name='contenido' style='visibility:hidden' style='width:100%'>".$row['ar_contenido']."</textarea></td></tr><tr>	<td colspan='3'><center><input type='submit' value='CAMBIAR' name='CAMBIAR' class='color1'></center></td></tr>"; }$r.="</table></form></div>";echo $r;Desconectar($con);}?>