<?phpinclude_once("../include/configSQL.php");include_once("../include/funciones.php");switch ($_REQUEST['funcion']) {    case "editar":        Editar($_REQUEST['id']);        break;}function Editar($id){$con=Conectar();$sql="select * from usuario where user_id=$id ";$result=mysql_query($sql);$r="<div class='box'><h2>Editar</h2><div class='content-separator'></div><form name='FrmEditarAlumno' id='FrmEditarAlumno' method='post'  onSubmit='return ValidaRegistroE()' ><table class='data-table'><input type='hidden' name='modulo' value='usuario'><input type='hidden' name='accion' value='actualizar'><input type='hidden' name='id' value='".$id."'>";while($row = mysql_fetch_array($result)){$r.="<tr>	<td><center><strong>*Nombre:</strong></center></td>	<td><input type='text' name='nombre' id='nombre' size='80' value='".$row['user_nombre']."'></td></tr><tr>	<td><center><strong>Usuario:</strong></center></td>	<td>".$row['user_usuario']."</td></tr><tr>	<td><center><strong>Correo:</strong></center></td>	<td><input type='text' name='correo' id='email' size='70' value='".$row['user_correo']."'></td></tr><tr>	<td><center><strong>Genero:</strong></center></td>	<td>".GeneroSelect($row['user_genero'])."</td></tr><tr>	<td><center><strong>Fecha Nacimiento:</strong></center></td>	<td><input id='fecha_1'  type='text' maxlength='13' size='12' name='fecha_nacimiento' onclick='LoadFecha();' readonly='' value='".$row['user_fecha_nacimiento']."'></td></tr><tr>	<td><center><strong>Rol:</strong></center></td>	<td>".RolSelect($row['user_rol'])."</td></tr><tr>	<td><center><strong>Cantidad Vehiculos:</strong></center></td>	<td><input type='text' name='cant_veh' id='email' size='10' value='".$row['user_pv_num_veh']."'></td></tr><tr>	<td colspan='3'><center><input type='submit' value='CAMBIAR' name='CAMBIAR' class='boton'></center></td></tr>"; }$r.="</table></form></div>";echo $r;Desconectar($con);}?>