<?phpinclude_once("../include/configSQL.php");include_once("../include/funciones.php");switch ($_REQUEST['funcion']) {    case "editar-categoria":        EditarCat($_REQUEST['id']);        break;	case "editar-menu":        EditarMenu($_REQUEST['id']);        break;		case "editar-plantilla":        EditarPlantilla($_REQUEST['id']);        break;		}function EditarPlantilla($id){$con=Conectar();$sql="select * from plantilla where id=$id";$result=mysql_query($sql);$r="<h3>Editar</h3><div class='content-separator'></div><form name='FrmEditarPla' id='FrmEditarPla' method='post' action='?' ><table class='data-table'><input type='hidden' name='modulo' value='web'><input type='hidden' name='accion' value='actualizar-plantilla'><input type='hidden' name='id' value='".$id."'>";while($row = mysql_fetch_array($result)){$t='document.getElementById("c_v").style.display="none"';$r.="<tr>							<td><center><strong>*Nombre:</strong></center></td>							<td><input type='text' name='nombre' id='nombre' size='80' value='".$row['nombre']."'></td>						</tr>						<tr>							<td><center><strong>*Alias:</strong></center></td>							<td><input type='text' name='alias' id='alias' size='100' value='".$row['alias']."'></td>						</tr><tr>	<td colspan='2'><span id='c_v' ><input class='boton' type='button' onclick='".$t.";CargarTimyMCE()' style='float: right;' value='EDITAR'>".$row['contenido']."</span><textarea name='contenido' style='visibility:hidden' style='width:100%'>".$row['contenido']."</textarea></td></tr><tr>	<td colspan='3'><center><input type='submit' value='CAMBIAR' name='CAMBIAR' class='boton' onclick='javascript: return ValidaPlantilla();'></center></td></tr>"; }$r.="</table></form>";echo $r;Desconectar($con);}function EditarMenu($id){$con=Conectar();$sql="select * from menu where men_id=$id ";$result=mysql_query($sql);$r="<div class='box'><h2>Editar</h2><div class='content-separator'></div><form name='FrmEditarMenu' id='FrmEditarMenu' method='post' action='?' ><table class='data-table'><input type='hidden' name='modulo' value='web'><input type='hidden' name='accion' value='actualizar-menu'><input type='hidden' name='id' value='".$id."'>";while($row = mysql_fetch_array($result)){$r.="						<tr>							<td><center><strong>Nombre:</strong></center></td>							<td><input class='input' type='text' name='nombre' id='nombre' size='65' value='".$row['men_nombre']."'></td>						</tr>						<tr>							<td><center><strong>Link:</strong></center></td>							<td><input class='input' type='text' name='link' id='link' size='70' value='".$row['men_link']."'></td>						</tr>						<tr>							<td><center><strong>Imprimir en Plantilla:</strong></center></td>							<td>".SiNoSelect($row['men_imp'],"imp")."</td>						</tr>												<tr>							<td colspan='3'><center><input type='submit' value='CAMBIAR' name='CAMBIAR' class='boton'></center></td>						</tr>	"; }$r.="</div>";echo $r;Desconectar($con);}function EditarCat($id){$con=Conectar();$sql="select * from categoria where id=$id ";$result=mysql_query($sql);$r="<h3>Editar</h3><div class='content-separator'></div><form name='FrmEditarCategoria' id='FrmEditarCategoria' method='post' action='?' ><table class='data-table'><input type='hidden' name='modulo' value='web'><input type='hidden' name='accion' value='actualizar-categoria'><input type='hidden' name='id' value='".$id."'>";while($row = mysql_fetch_array($result)){if($row['visible']=="s"){ $s="<option value='s' selected >Visible</option> <option value='n' >No Visible</option>";}elseif($row['visible']=="n"){$s="<option value='s' >Visible</option>	<option value='n' selected >No Visible</option>";}$r.="						<tr>							<td><center><strong>Titulo:</strong></center></td>							<td><input type='text' name='titulo' id='titulo' size='80' value='".$row['titulo']."'></td>						</tr>						<tr>							<td><center><strong>Nombre:</strong></center></td>							<td><input type='text' name='categoria' id='categoria' size='80' value='".$row['categoria']."'></td>						</tr>						<tr>							<td><center><strong>Alias:</strong></center></td>							<td><input type='text' name='alias' id='alias' size='70' value='".$row['alias']."' ></td>						</tr>						<tr>							<td><center><strong>Visible:</strong></center></td>							<td>								<select id='visible' name='visible'>								$s								</select>							</td>						</tr>						<tr>							<td colspan='3'><center><input type='submit' value='CAMBIAR' name='CAMBIAR' class='color1'></center></td>						</tr>	"; }echo $r;Desconectar($con);}?>